var futs = newTable[int, TdlibEntry]()

template waitFut*(client, kind, field: untyped): untyped = 
  let fut = newFuture[TdlibFuture]()
  futs[client.counter-1] = (kind, fut)
  let tdlibFut = await fut
  tdlibFut.field

import strutils

proc completeFut*(event: sink JsonNode) = 
  # TODO: Ideally this check shouldn't be there and *all* events we receive
  # should have that field (except updates of course)
  if "@extra" notin event:
    return
  # Get the index of the future
  if event["@type"].getStr() == "error":
    let err = event.toCustom(Error)
    raise newException(
      ValueError, "Tdlib error code $1, message: $2" % [$err.code, err.message]
    )
  let idx = parseInt(event["@extra"].getStr())
  event.delete("@extra")
  let waitingFut = futs[idx]
  var completedFut = TdlibFuture(kind: waitingFut.kind)
  case completedFut.kind
  of tfBasicGroup:
    completedFut.basicgroup = event.toCustom(BasicGroup)
  of tfRecoveryEmailAddress:
    completedFut.recoveryemailaddress = event.toCustom(RecoveryEmailAddress)
  of tfPaymentReceipt:
    completedFut.paymentreceipt = event.toCustom(PaymentReceipt)
  of tfImportedContacts:
    completedFut.importedcontacts = event.toCustom(ImportedContacts)
