    completedFut.checkchatusernameresult = event.toCustom(CheckChatUsernameResult)
  of tfChatMember:
    completedFut.chatmember = event.toCustom(ChatMember)
  of tfJsonValue:
    completedFut.jsonvalue = event.toCustom(JsonValue)
  of tfInlineQueryResults:
    completedFut.inlinequeryresults = event.toCustom(InlineQueryResults)
