  ## **only_missed** - If true, returns only messages with missed calls
  client.send %*{
    "@type": "searchCallMessages" , 
    "from_message_id": fromMessageId, 
    "limit": limit, 
    "only_missed": onlyMissed
  }
  result = client.waitFut(tfMessages, messages)

