proc getChatEventLog*(client: TdlibClient, chatId: int64, query: string, fromEventId: string, limit: int32, filters: ChatEventLogFilters, userIds: seq[int32]): Future[ChatEvents] {.async.} = 
  ## Returns a list of service actions taken by chat members and administrators in the last 48 hours. Available only for supergroups and channels. Requires administrator rights. Returns results in reverse chronological order (i. e., in order of decreasing event_id)
  ## **chat_id** - Chat identifier
  ## **query** - Search query by which to filter events
  ## **from_event_id** - Identifier of an event from which to return results. Use 0 to get results from the latest events
  ## **limit** - The maximum number of events to return; up to 100
  ## **filters** - The types of events to return. By default, all types will be returned
  ## **user_ids** - User identifiers by which to filter events. By default, events relating to all users will be returned
  client.send %*{
    "@type": "getChatEventLog" , 
    "chat_id": chatId, 
    "query": query, 
    "from_event_id": fromEventId, 
    "limit": limit, 
    "filters": filters, 
    "user_ids": userIds
  }
  result = client.waitFut(tfChatEvents, chatevents)

proc addChatMember*(client: TdlibClient, chatId: int64, userId: int32, forwardLimit: int32): Future[Ok] {.async.} = 
  ## Adds a new member to a chat. Members can't be added to private or secret chats. Members will not be added until the chat state has been synchronized with the server
  ## **chat_id** - Chat identifier
  ## **user_id** - Identifier of the user
  ## **forward_limit** - The number of earlier messages from the chat to be forwarded to the new member; up to 100. Ignored for supergroups and channels
  client.send %*{
    "@type": "addChatMember" , 
    "chat_id": chatId, 
    "user_id": userId, 
    "forward_limit": forwardLimit
  }
  result = client.waitFut(tfOk, ok)

proc getChat*(client: TdlibClient, chatId: int64): Future[Chat] {.async.} = 
