  result = client.waitFut(tfStickerSets, stickersets)

proc addNetworkStatistics*(client: TdlibClient, entry: NetworkStatisticsEntry): Future[Ok] {.async.} = 
  ## Adds the specified data to data usage statistics. Can be called before authorization
